import React, { useState } from 'react';

function Form() {
  const [alienRegistrationNumber, setAlienRegistrationNumber] = useState(['A-', '', '', '', '', '', '', '', '', '']);

  const handleSectionChange = (e, index) => {
    const newValue = e.target.value;
    const newRegistrationNumber = [...alienRegistrationNumber];
    newRegistrationNumber[index] = newValue;
    setAlienRegistrationNumber(newRegistrationNumber);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedNumber = alienRegistrationNumber.join('');
    // Send formattedNumber to the backend for processing
    console.log(formattedNumber); // For demonstration purposes, log the data
  };

  return (
    <div>
      <h2>Immigration Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="countryOfBirth">Country of Birth:</label>
          <input
            type="text"
            id="countryOfBirth"
            name="countryOfBirth"
            // Add value and onChange as needed for other fields
          />
        </div>

        <div>
          <label htmlFor="streetNumber">Street Number and Name:</label>
          <input
            type="text"
            id="streetNumber"
            name="streetNumber"
            // Add value and onChange as needed for other fields
          />
        </div>

        <div>
          <label htmlFor="alienRegistrationNumber">Alien Registration Number:</label>
          <div className="alien-registration-number">
            {alienRegistrationNumber.map((section, index) => (
              <input
                type="text"
                key={index}
                value={section}
                onChange={(e) => handleSectionChange(e, index)}
                maxLength="1"
                pattern="\d"
                required={index === 1} // Require the second section
              />
            ))}
          </div>
        </div>

        {/* Add more form fields here */}

        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
}

export default Form;
