import React, { useState } from 'react';

function Form() {
  const [alienRegistrationNumber, setAlienRegistrationNumber] = useState(['', '', '', '', '', '', '', '', '', '']);

  const handleSectionChange = (e, index) => {
    const newValue = e.target.value;

    // Validate the input to allow only digits from 0 to 9
    if (/^[0-9]$/.test(newValue)) {
      const newRegistrationNumber = [...alienRegistrationNumber];
      newRegistrationNumber[index] = newValue;
      setAlienRegistrationNumber(newRegistrationNumber);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedNumber = 'A-' + alienRegistrationNumber.join('');
    // Send formattedNumber to the backend for processing
    console.log(formattedNumber); // For demonstration purposes, log the data
  };

  return (
    <div>
      <h2>Immigration Form</h2>
      <form onSubmit={handleSubmit}>

        <div>
          <label htmlFor="alienRegistrationNumber">Alien Registration Number:</label>
          <div className="alien-registration-number">
            <span style={{ fontWeight: 'bold', marginRight: '5px' }}>A-</span>
            {alienRegistrationNumber.map((section, index) => (
              <input
                type="text"
                key={index}
                value={section}
                onChange={(e) => handleSectionChange(e, index)}
                maxLength="1"
                pattern="\d"
                required={index === 0} // Require the first section
                style={{
                  width: '20px', // Set the width for each digit input
                  marginRight: '5px', // Add some spacing between digits
                  textAlign: 'center', // Center-align the digit
                }}
              />
            ))}
          </div>
        </div>

        <div>
          <label htmlFor="countryOfBirth">Country of Birth:</label>
          <input
            type="text"
            id="countryOfBirth"
            name="countryOfBirth"
            // Add value and onChange as needed for other fields
          />
        </div>

        <div>
          <label htmlFor="provinceOfBirth">Province of Birth:</label>
          <input
            type="text"
            id="provinceOfBirth"
            name="provinceOfBirth"
            // Add value and onChange as needed for other fields
          />
        </div>

        <div>
          <label htmlFor="countryOfCitizensh">Country of Citizenship or Nationality:</label>
          <input
            type="text"
            id="provinceOfBirth"
            name="provinceOfBirth"
            // Add value and onChange as needed for other fields
          />
        </div>


        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
}

export default Form;
