import React, { useState, useRef } from 'react';
import ProcessingInformation from './ProcessingInformation'; // Import the ProcessingInformation component
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function Form() {
  const alienRegistrationInputRefs = useRef([]);
  const arrivalDepartureInputRefs = useRef([]);

  const [alienRegistrationNumber, setAlienRegistrationNumber] = useState(['', '', '', '', '', '', '', '', '', '']);
  const [arrivalDepartureRecordNumber, setArrivalDepartureRecordNumber] = useState(['', '', '', '', '', '', '', '', '', '', '']);

  const handleAlienRegistrationChange = (e, index) => {
    const newValue = e.target.value;
    if (/^[0-9]$/.test(newValue)){

      // Automatically focus on the next input field if not the last one
      if (index < alienRegistrationInputRefs.current.length - 1 && newValue.length === 1) {
        alienRegistrationInputRefs.current[index + 1].focus();
      }
    }
  };

  const handleArrivalDepartureChange = (e, index) => {
    const newValue = e.target.value;

    // Validate the input to allow only digits from 0 to 9
    if (/^[0-9]$/.test(newValue)) {
      const newDigit = [...arrivalDepartureRecordNumber];
      newDigit[index] = newValue;
      setArrivalDepartureRecordNumber(newDigit);

      // Automatically focus on the next input field if not the last one
      if (index < arrivalDepartureInputRefs.current.length - 1 && newValue.length === 1) {
        arrivalDepartureInputRefs.current[index + 1].focus();
      }
    }
  };

  const handleNext = (e) => {
    e.preventDefault();
    const formattedAlienRegistrationNumber = 'A-' + alienRegistrationNumber.join('');
    const formattedArrivalDepartureRecordNumber = arrivalDepartureRecordNumber.join('');
    // Send formattedAlienRegistrationNumber and formattedArrivalDepartureRecordNumber to the backend for processing
    console.log(formattedAlienRegistrationNumber);
    console.log(formattedArrivalDepartureRecordNumber);
  };

   return (
    <div>
      <h2>Immigration Form</h2>
      <form onSubmit={handleNext}>
        {/* Alien Registration Number */}
        <div>
          <label htmlFor="alienRegistrationNumber">Alien Registration Number:</label>
          <div className="alien-registration-number">
            <span style={{ fontWeight: 'bold', marginRight: '5px' }}>A-</span>
            {alienRegistrationNumber.map((section, index) => (
              <input
                type="text"
                key={index}
                value={section}
                onChange={(e) => handleAlienRegistrationChange(e, index)}
                maxLength="1"
                pattern="\d"
                required={index === 0} // Require the first section
                style={{
                  width: '20px', // Set the width for each digit input
                  marginRight: '5px', // Add some spacing between digits
                  textAlign: 'center', // Center-align the digit
                }}
                ref={(el) => (alienRegistrationInputRefs.current[index] = el)}
              />
            ))}
          </div>
        </div>

        {/* Country of Birth */}
        <div>
          <label htmlFor="countryOfBirth">Country of Birth:</label>
          <input
            type="text"
            id="countryOfBirth"
            name="countryOfBirth"
            // Add value and onChange as needed for other fields
          />
        </div>

        {/* Province of Birth */}
        <div>
